/* *****************************************AUTHOR Paul CurzonThis program demonstrates  - arrays.   Fill an array using a for loop as the result of a calculation   ******************************************** */import javax.swing.*; // import the swing library for I/Oclass lookupcube{    public static void main (String[] param)    {                createLookup();        System.exit(0);            } // END main   /* ***************************************************       Define some commands of our own to use above   *************************************************** */            /* ***************************************************       Set up an array contianing the number 5  everywhere then print it    */        public static void createLookup()    {       // Declare variables       //              int[] lookup = new int[10];       String querytext;       int query;       //first fill an array with the answer to a calculation for each value (cubing)       for(int i = 0; i<lookup.length; i++)       {           lookup[i] = i*i*i;       }                   //now can get an answer quickly without calculating just looking it up       querytext = JOptionPane.showInputDialog("Give me a number to cube quickly");       query = Integer.parseInt(querytext);              JOptionPane.showMessageDialog(null, query + " cubed is " + lookup[query]);                } // END createLookup    } // END class lookupcube