/* *****************************************AUTHOR Paul CurzonThis program demonstrates  - arrays loops.   Print the contents of an array   ******************************************** */import javax.swing.*; // import the swing library for I/Oclass printarray{    public static void main (String[] param)    {                printArray();        System.exit(0);            } // END main   /* ***************************************************       Define some commands of our own to use above   *************************************************** */            /* ***************************************************       Set up an array then print it    */        public static void printArray()    {       // Declare variables       //              int[] fourTimes = {0,4,8,12,16,20,24,28,32,36,40};               for(int i = 0; i<11; i++)       {           System.out.println(i + ":" + fourTimes[i]);       }          } // END printArray    } // END class printarray 