/* *****************************************AUTHOR Paul CurzonThis program demonstrates  - arrays. - binary search - recursion   Search for a given integer in an ORDERED array.   ******************************************** */import javax.swing.*; // import the swing library for I/Oclass recbinarysearch{    public static void main (String[] param)    {                search();        System.exit(0);            } // END main   /* ***************************************************       Define some commands of our own to use above   *************************************************** */            /* ***************************************************       Set up an array containing animals then find one asked for by the user    */        public static void search()    {       // Declare variables       //              int [] data = {1,2,3,5,8,13,21,34};       String searchKeyText;       int searchKey; //the thing looked for       int result = -1;        // the position where the answer is found        //now can get an answer quickly without calculating just looking it up       searchKeyText = JOptionPane.showInputDialog("Give me a number to search for");       searchKey = Integer.parseInt(searchKeyText);              result = binary_search (searchKey, data, 0, data.length - 1);              if (result == -1)         {JOptionPane.showMessageDialog(null, "Sorry its not there"); }              else         {JOptionPane.showMessageDialog(null, searchKey + " was found in position " + result); }                } // END search        public static int binary_search (int key, int[] array, int low, int high)    {       int mid = (low+high)/2;       if (high < low)          { return -1; }       else if (key == array[mid])          { return mid; }       else if (high == low)          { return -1; }       else if (key < array[mid])          { return  binary_search(key, array, low, mid-1); }       else          { return  binary_search(key, array, mid+1, high); }    }    } // END class binarysearch